INSERT INTO product VALUES ('1', '1', 'Japanese', 'img-2.jpg', '10.00', '<p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:107%\"><b>Japanese\r\nstrawberries</b> in Da Lat are provided daily by DaLaVi, receive strawberry orders\r\nat least 01 working day in advance to schedule picking, packing and shipping.<o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:107%\"><o:p>&nbsp;</o:p></span></p><ul><li><span style=\"font-size:12.0pt;line-height:107%\">Packing:\r\n500gr / box<o:p></o:p></span></li><li><span style=\"font-size:12.0pt;line-height:107%\">Retail\r\nprice: $10 / box of 49 left; $15 / box of 36 left; $17 / box of 30 fruits and\r\n20$ / box of 25 fruits<o:p></o:p></span></li><li><span style=\"font-size:12.0pt;line-height:107%\">PRICE ONLY:\r\ndepending on the size and change from time to time, please leave your\r\ninformation or contact us directly!<o:p></o:p></span></li><li><span style=\"font-size:12.0pt;line-height:107%\">Storage: 3-4\r\ndays depending on temperature conditions, recommended to prevent refrigeration\r\nimmediately after receiving strawberries from DaLaVi<o:p></o:p></span></li></ul><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><ul><li><span style=\"font-size:12.0pt;line-height:107%\">Growing\r\narea: Da Lat-Vietnam</span><o:p></o:p></li></ul>', '<p class=\"MsoNormal\"><span style=\"font-size:12.0pt;line-height:107%\"><b>Japanese\r\nstrawberries</b> are red, succulent, sweet and sweet and have a rich sweet taste,\r\ndifferent from all other strawberries being grown in Da Lat and surrounding\r\nareas today:</span><o:p></o:p></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat-1.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2050\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat-1.jpg\" alt=\"\" width=\"700\" height=\"933\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif; text-align: center;\"><span style=\"font-size:14.0pt;line-height:107%;\r\nfont-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-theme-font:minor-latin;color:#222222;background:#F8F9FA;mso-ansi-language:\r\nEN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">Dalat Japanese\r\nstrawberries - mesmerizing from the delicious taste!</span><br></p><ul style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><li><span style=\"font-size:14.0pt;line-height:107%;\r\nfont-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-theme-font:minor-latin;color:#222222;background:#F8F9FA;mso-ansi-language:\r\nEN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">Packing: 500gr / box.\r\nSize: 25 fruits / box, 30 fruits, 36 fruits and 49 fruits</span><br></li><li><p class=\"MsoNormal\"><span style=\"font-size:14.0pt;line-height:107%;mso-bidi-font-family:\r\nCalibri;mso-bidi-theme-font:minor-latin;color:#222222;background:#F8F9FA\">Price:\r\nchange from time to time, please leave information or contact us directly!<o:p></o:p></span></p></li><li><p class=\"MsoNormal\"><span style=\"font-size:14.0pt;line-height:107%;mso-bidi-font-family:\r\nCalibri;mso-bidi-theme-font:minor-latin;color:#222222;background:#F8F9FA\">Storage:\r\n3-4 days depending on temperature conditions, recommended to prevent\r\nrefrigeration immediately after receiving strawberries from DaLaVi<o:p></o:p></span></p></li><li><p class=\"MsoNormal\"><span style=\"font-size:14.0pt;line-height:107%;mso-bidi-font-family:\r\nCalibri;mso-bidi-theme-font:minor-latin;color:#222222;background:#F8F9FA\">Growing\r\narea: Da Lat-Vietnam</span><span style=\"font-size:14.0pt;line-height:107%;\r\nmso-bidi-font-family:Calibri;mso-bidi-theme-font:minor-latin\"><o:p></o:p></span></p></li></ul><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-25-trai.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2098\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-25-trai.jpg\" alt=\"\" width=\"700\" height=\"700\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-30-trai.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2099\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-30-trai.jpg\" alt=\"\" width=\"700\" height=\"700\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-36-trai.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2100\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-36-trai.jpg\" alt=\"\" width=\"701\" height=\"701\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-49-trai.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2101\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/size-49-trai.jpg\" alt=\"\" width=\"700\" height=\"700\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><span style=\"font-size:14.0pt;line-height:107%;\r\nfont-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-font-family:&quot;Times New Roman&quot;;mso-bidi-theme-font:minor-bidi;\r\nmso-ansi-language:EN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">Japanese\r\nstrawberries are considered as high-class strawberries currently grown in Da\r\nLat. If before, when it comes to strawberries, Vietnamese people only think and\r\nknow about American rock or New Zealand strawberries, now: cherry gardens like\r\nJapanese, red and red are always hunted and checked-in every time. set foot in\r\nthe land of fog.</span><br></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2054\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat.jpg\" alt=\"\" width=\"700\" height=\"525\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p class=\"MsoNormal\"><span style=\"font-family:&quot;Arial&quot;,sans-serif;color:#222222;background:#F8F9FA\"><span style=\"white-space: pre-wrap;\">Japanese strawberry varieties have been grown in Da Lat from about 5 years ago</span></span>.<o:p></o:p></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Despite its worldwide popularity, the most surprising thing is:\r\nstrawberries are inherently not native to Japan. However, just like ramen and\r\npasta - imported specialties from abroad are transformed into their own\r\nnational dishes by the Japanese - strawberries are also bred and nurtured by\r\nthe Japanese. Strawberry varieties, from shape to taste.</span><o:p></o:p></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat-2.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2051\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-da-lat-2.jpg\" alt=\"\" width=\"700\" height=\"933\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp; &nbsp;Japanese strawberries are carefully packed and can travel\r\nlong distances</span><o:p></o:p></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">With a plump, glossy and round appearance, soft flesh that\r\ncan bite down to the teeth and an enchanting sweet taste and always succulent,\r\n... It can be said that strawberries are one of the favorite fruits. most in\r\nJapan. As of 2013, Japan is the country with the 7th largest strawberry\r\nproduction in the world with almost all of its production consumed\r\ndomestically. You can see this fruit appearing in all areas of Japanese life,\r\nfrom food (especially sweets), to fashion, culture, movies, manga, anime, ...\r\nAn indispensable food for Christmas in Japan is fresh strawberry cream cake. In\r\naddition, fresh strawberry dumplings coated with red beans (Ichigo Daifuku) are\r\nalso one of the most popular sweets in Japan.</span><o:p></o:p></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">The most interesting thing about Japanese strawberries is the\r\namazing number of strawberry varieties with new varieties introduced almost\r\nevery year. Among them, there are also extremely expensive varieties of\r\nstrawberries, such as a unique white strawberry called “Hatsukoi no Kaori,\r\nwhich costs up to </span><span lang=\"VI\" style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">$11</span><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">/ fruit.<o:p></o:p></span></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\">\r\n\r\n</p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Strawberries are grown all over Japan. Each province has a\r\ndifferent variety of strawberries, creating the diversity in the market and the\r\nspecific products of each locality. Some of the most famous strawberry\r\nprovinces in Japan are Tochigi, Fukuoka, Kumamoto, Nagasaki, ...</span><o:p></o:p></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><em>1) Tochi Otome (</em><span style=\"font-family: Arial, sans-serif; font-size: 1rem;\">the provincial&nbsp;</span><em style=\"font-size: 1rem;\">Tochigi)</em></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-10.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2057\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-10.jpg\" alt=\"\" width=\"701\" height=\"465\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Tochi Otome strawberry varieties of Tochigi prefecture when\r\nripe bright red, the fruit is small and elongated, but has a very seductive\r\nfragrance. The name Tochi Otome, meaning \"Lady of Tochigi\", partly\r\nshows the love of Tochigi people for her most famous strawberry variety.</span><o:p></o:p></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><em>2) Amao (</em><span style=\"font-family: Arial, sans-serif;\">the provincial</span><em>&nbsp;Fukuoka)</em></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-1.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2055\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-1.jpg\" alt=\"\" width=\"700\" height=\"394\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif; text-align: center;\"><em>(Nguồn ảnh: www.fukuoka-now.com)</em></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">The name \"Amao\" means \"Sweet King\". The\r\nFukuoka prefecture\'s Amao strawberry variety is round in shape and is 4-5 times\r\nthe size of a regular strawberry. On average, Amao strawberries weigh between\r\n20 and 30 grams per fruit.<o:p></o:p></span></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\">\r\n\r\n</p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">3) Hatsukoi no Kaori</span><o:p></o:p></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><a href=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-3.jpg\" style=\"color: rgb(0, 166, 81); transition: all 0.3s ease 0s;\"><img class=\"aligncenter wp-image-2056\" src=\"https://dautaydalat.vn/wp-content/uploads/2020/02/dau-tay-nhat-ban-3.jpg\" alt=\"\" width=\"700\" height=\"525\" style=\"max-width: 100%; height: auto; display: block; margin-left: auto; margin-right: auto;\"></a></p><p style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif; text-align: center;\"><em>(Nguồn ảnh: www.miyoshi-group.co.jp)</em></p><p class=\"MsoNormal\"><span style=\"font-family: Arial, sans-serif; color: rgb(33, 37, 41); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">This strawberry variety is the result of the Miyoshi\r\nAgri-Tech Co laboratory with its unique white color and superb sweetness. This\r\nis also one of the rare strawberry varieties in Japan, so the price is\r\nstaggering - more than $ 12 / fruit. Because its name means \"The taste of\r\nfirst love\", many people choose to buy it as a gift for their lover /\r\npartner.</span><o:p></o:p></p>', '0', '2020-08-17 21:25:28', '2020-08-17 21:25:28');
INSERT INTO product VALUES ('2', '1', 'Mangosteen', 'img-3.jpg', '20.00', '<p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: &quot;Roboto Condensed&quot;, sans-serif; font-size: 15px;\">Măng cụt thái lan giá rẻ thực tế là bởi đã được khuyến mãi để mừng Ưu Đàm ra mắt chi nhánh thứ 3 tại Hà Đông</p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; color: rgb(51, 51, 51); font-family: &quot;Roboto Condensed&quot;, sans-serif; font-size: 15px;\">Kính mời quý khách hàng đặt hàng loại trái cây được rất nhiều người yêu thích này ạ.</p>', '<h2 style=\"font-family: Helvetica, Arial, sans-serif; font-weight: inherit; line-height: inherit; color: rgb(0, 0, 0); margin-right: 0px; margin-bottom: 0px; margin-left: 0px; font-size: inherit; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; vertical-align: top;\"><br></h2><p class=\"MsoNormal\"><span lang=\"VI\" style=\"font-size: 16pt; line-height: 107%; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Măng Cụt Mua Ở Đâu?</span><span lang=\"VI\" style=\"font-size:16.0pt;line-height:107%;mso-ansi-language:VI\"><o:p></o:p></span></p><p style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Helvetica, Arial, sans-serif; vertical-align: top;\">&nbsp;</p><p style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Helvetica, Arial, sans-serif; vertical-align: top; text-align: center;\"><a href=\"https://okfo